import styled from 'styled-components'

export const Filter = styled.section`
  border-top: 1px solid #e3e3e3;
  border-bottom: 1px solid #e3e3e3;
  @media (max-width: 1200px) {
    display: none;
  }
`

export const FilterList = styled.ul`
  display: flex;
`
