import styled from 'styled-components'

export const MainProducts = styled.main`
  margin: 0 auto;
  width: 80%;
  max-width: 1100px;
  padding: 16px;
`

export const Products = styled.section`
  margin-top: 20px;
  margin-bottom: 20px;
`

export const ProductsList = styled.ol`
  display: flex;
  flex-wrap: wrap;
  justify-content: space-between;
`
