import styled from 'styled-components'

export const Item = styled.li``

export const Link = styled.a`
  text-decoration: none;
  font-size: 0.8125rem;
  font-weight: 600;
  color: #000;
  text-transform: uppercase;
  padding-bottom: 5px;
  vertical-align: baseline;
  &:hover {
    border-bottom: 2px solid #000;
  }
`
